UPDATE carteraeps t1 
                SET t1.ValorOriginal=(SELECT ValorOriginal FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),
                    t1.FechaRadicado=(SELECT FechaFactura FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),
                    t1.MesServicio=(SELECT MesServicio FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),
                    t1.NumeroRadicado=(SELECT NumeroRadicado FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),
                    t1.NumeroOperacion=(SELECT NumeroOperacion FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1), 
                    t1.TipoOperacion=(SELECT TipoOperacion FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),    
                    t1.ValorMenosImpuestos=(SELECT ValorMenosImpuestos FROM historial_carteracargada_eps t2 WHERE t1.NumeroFactura=t2.NumeroFactura AND EXISTS (SELECT 1 FROM ts_eps.tipos_operacion t3 WHERE t2.TipoOperacion = t3.TipoOperacion AND Aplicacion='FACTURA' ) ORDER BY t2.FechaFactura DESC LIMIT 1),
                    t1.TipoNegociacion = IF(MOD(t1.TipoOperacion,2) = 0, 'EVENTO', 'CAPITA');